import React, { Component } from "react";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { List, ListItem } from "../components/List";
import AchievementCard from "../components/AchievementCard";
import { Z_BLOCK } from "zlib";

class Achievement extends Component {
  state = {
    achievements: [],
    type: "",
    achivementPoints: 0,
    summary: "",
    date: "",
    earned: ""
  };

  componentDidMount() {
    this.loadAchievements();
  }

  loadAchievements = () => {
    API.getAchievements()
      .then(res =>
        this.setState({
          achievements: res.data,
          type: "",
          achievementPoints: 0,
          summary: "",
          date: "",
          earned: ""
        })
      )
      .catch(err => console.log(err));
  };

  deleteAchievement = id => {
    API.deleteAchievement(id)
      .then(res => this.loadAchievements())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  render() {
    return (
      <div>
        <h2>Total Points: 0{this.getPoints}</h2>
        <div className="row">
          <div className="col s9 m9">
            {this.state.achievements.map(achievement => (
              <Link to={"/achievements/" + achievement._id}>
                <AchievementCard key={achievement._id}>
                  <div className="card-image">
                    <img src="/images/wellness.jpeg" />
                    <a className="btn-floating halfway-fab waves-effect waves-light red">
                      <i
                        className="material-icons"
                        onClick={() => this.deleteAchievement(achievement._id)}
                      >
                        add
                      </i>
                    </a>
                  </div>
                  <span className="card-title">
                    Redeem {achievement.summary} Points
                  </span>
                  {achievement.achievementPoints}
                </AchievementCard>
              </Link>
            ))}
          </div>

        </div>
      </div>
    );
  }
}

export default Achievement;
